import { ethers } from "hardhat";
import { expect } from "chai";
import { Reverter } from "@/test/helpers/";

import { CECDSA384Signer } from "@ethers-v6";

describe("CECDSA384Signer", () => {
  const reverter = new Reverter();

  let signerSha2: CECDSA384Signer;

  before("setup", async () => {
    const CECDSA384Signer = await ethers.getContractFactory("CECDSA384Signer");

    signerSha2 = await CECDSA384Signer.deploy();

    await signerSha2.__CECDSA384Signer_init(true, true);

    await reverter.snapshot();
  });

  afterEach(reverter.revert);

  describe("#verifyICAOSignature", () => {
    it("should verify icao signature using SHA2", async () => {
      const signedAttributes =
        "";
      const icaoMemberSignature =
        "0x3044b552135e5eb46368e739b3138f9f1f2eb37a0518f564d2767d02ac67a9f41fb71bad06a99f54ee2e43ead2916f630e07a31eb5214798e5ecb032e49585f5d3d52b6f74d8bd71fbfd606a4466ae7a33723520475d1367c1a35e30a0e80a96";
      const icaoMemberKey =
        "0x56931fd7d42942eec92298d7291371cdbac29c60230c9f635d010939ab7f8f5d977ccfe90bd7528cafa53afad6225bf61e2af4d20831aed1e6b578ccb00e1534182f6d1ee6bf524fbd62bd056d0d538c24eb7f2a436e336e139f00a072b0ba1a";

      expect(await signerSha2.verifyICAOSignature(signedAttributes, icaoMemberSignature, icaoMemberKey)).to.be.true;
    });
  });
});
